configuration.source:
if (project.version.endsWith("-SNAPSHOT")){
  project.properties['deploy.url'] = project.distributionManagement.snapshotRepository.url;
  project.properties['dist.deploy.groupId'] = "com.continuuity.distributions.snapshots";
} else {
  project.properties['deploy.url'] = project.distributionManagement.repository.url;
  project.properties['dist.deploy.groupId'] = "com.continuuity.distributions.release";
}
